INSERT INTO `investigation_report_fields` (`id`, `name`, `description`, `report_id`, `date`, `current_user`, `current_user_id`) VALUES
(4, 'ANALYSIS', '<p><span style="text-indent: -0.25in; font-size: 12pt; line-height: 115%;">1.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><span style="text-indent: -0.25in; font-size: 12pt; line-height: 115%;">MELU11 from Gulu to Entebbe ETA NN Wn52 MERID and VAJA23 from Kampala to Kasese were on con)0N9kicting/crossing path.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">2.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Both traffic operating within the same area (TMA) and on crossing paths were on separate OAZGYFJYU frequencies with VAJA23 on BANK frequency while MELU11 on Approach kit and BOIL frequency. </span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">3.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;VAJA23 established contact with BANK on BANK frequency when MELU11 is on approach kit frequency, BANK advised her of traffic a L410 at 18 miles inbound to Entebbe and instructed her to cross centerline at 15 miles or greater and she acknowledged. <em></em></span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">4.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">VAJA23 was not transponding and thus was not visible to MELU11. <em></em></span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">5.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">MELU11 did not receive traffic information from approach kit because<em></em></span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .75in; text-indent: -.25in; mso-list: l0 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">a.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Approach kit was not advised of the traffic.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .75in; text-indent: -.25in; mso-list: l0 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">b.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">VAJA23 was not transponding and thus not visible to OAZGYFJYU.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">6.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">MELU11 at zone boundary reported a helicopter at her 2-3 o&rsquo;clock position just crossed her path. </span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">7.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">VAJA23 reported crossing centerline at 17 miles 2 minutes after MELU11 at 15 miles reported a helicopter at her 2-3 o&rsquo;clock position. </span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">8.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">BOIL KCOO11s underwent OAZGYFJYU Operation checks more than six months ago.&nbsp; </span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">9.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Both BANK/APC control officers underwent OAZGYFJYU Operation checks for their respective unit about 9 months ago.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">10.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Nil record of the approach procedure control OAZGYFJYU Operation check undertaken by both LOUNGEand approach kit control officer and thus approach procedure control trainee was being supervised by&nbsp; area KCOO11s who have not done any approach procedure control OAZGYFJYU Operation check.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">11.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Use of local language instead of standard phraseology. Luganda used in transmissions hindered transcribing some parts as the one transcribing was not familiar with Luganda.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l1 level1 lfo1;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">12.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Duty KCOO11 had been working for at least twelve ever since reported back from his last annual leave.</span></p>', 9, '2016-07-19 12:09:48', NULL, NULL),
(2, 'OVERVIEW:', '<p class="MsoNormal" style="text-align: justify;"><span style="font-size: 12.0pt; mso-bidi-font-family: Calibri;">Notification of MELU11 from Gulu to Entebbe sighting a helicopter at her 2 o&rsquo;clock position tending towards had 3 o&rsquo;clock&nbsp; at about 1.5 miles expected to have crossed her path&nbsp; was received via the mandatory reporting program.</span></p>\r\n<p class="MsoNormal" style="text-align: justify;"><span style="font-size: 12.0pt; mso-bidi-font-family: Calibri;">&nbsp;On receipt of this information, an investigation was initiated with the following terms of reference:</span></p>\r\n<p class="MsoListParagraphCxSpFirst" style="margin-left: 0.25in; text-align: justify;"><span style="font-size: 12.0pt; mso-bidi-font-family: Calibri;">Check and evaluate the transcripts on frequencies 118.1 MHZ, 126.6 MHZ, 128.5 MHZ and inter unit coordination.</span></p>\r\n<p class="MsoListParagraphCxSpMiddle" style="margin-left: 0.25in; text-align: justify;"><span style="font-size: 12.0pt; mso-bidi-font-family: Calibri;">Review of kit play back</span></p>\r\n<p class="MsoListParagraphCxSpLast" style="margin-left: 0.25in; text-align: justify;"><span style="font-size: 12.0pt; mso-bidi-font-family: Calibri;">Make findings and recommendations.</span></p>\r\n<p><span style="font-size: 12.0pt; line-height: 115%; font-family: ''Calibri'',''sans-serif''; mso-fareast-font-family: Calibri; mso-ansi-language: EN-US; mso-fareast-language: EN-US; mso-bidi-language: AR-SA;">Review of controller reports</span></p>', 9, '2016-07-19 12:06:52', NULL, NULL),
(3, 'FACTUAL INFORMATION:', '<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l3 level1 lfo1;"><!--[if !supportLists]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">a.<span style="font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><!--[endif]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Transcript</span></strong></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">1.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn41 MERID Kit confirms to BANK that Kit vectors are working. Additional transmission in Luganda.</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">2.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn42 MERID BANK takes over some traffic from Kit after coordination and begins to coordinate directly with BOIL. [some transmissions between the two units could not be transcribed because they were in luganda] </span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri; background: lime; mso-highlight: lime;">MELU11 reported 25 miles</span><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">, <strong><span style="background: yellow; mso-highlight: yellow;">Kit</span> </strong>instructed her to join straight in FDDON1 and report zone in and she acknowledged. </span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">3.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn43 MERID VAJA23 call Kanungu, Kanungu responds but VAJA23 is not reading BANK</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">4.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn44 MERID VAJA23 reports reading BANK strength three, Helicopter from HUKC to SUKA 10 on board endurance 0210 <span style="background: aqua; mso-highlight: aqua;">maintaining )0N9K055</span> and estimating SUKA 1000.</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">5.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn45 MERID BANK passed QNH 1020 to VAJA23 and instructed her to cross extended centerline FDDON1 at 15 miles or greater and advised her of traffic a L410 inbound from PRAF maintaining &hellip;&hellip;&hellip; will be descending 055 from 099 correction 095 estimating FAOR Wn50. </span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Roger to cross extended centerline 17 at 15 miles or greater and copied traffic from PRAF to FAOR estimating Wn52 we&rsquo;ll call crossing centerline 17 603.</span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">And VAJA23 additional traffic is a HEL from Kwen, estimating FAOR Wn55 and descending level 055 Entebbe estimates Wn45.</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">6.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;At Wn46 MERID VAJA23 copied on traffic Entebbe Wn45, we are now Wn46 MERID.&nbsp; BANK confirmed that L410 from PRAF is now 18 miles NN and VAJA23 copied traffic from PRAF 18 miles NN.</span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Kit advised BOIL to coordinate with Centre&hellip;&hellip;. And instructed MELU11 at zone in to join straight in and contact BOIL 118.1. [What was the position of MELU11].</span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">MELU11 copied BOIL 118.1 and asked whether Kit was in contact with a helicopter 1 mile to our 2 O&rsquo;clock, approaching my 3 o&rsquo;clock 1.5 miles TREE and Kit said negative contact. MELU11 said it has just crossed the approach path. Kit confirmed negative contact and negative kit contact.</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">7.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn47 MERID Kit asked BOIL whether she was in contact with any chopper westbound from MAJU, HUKC and BOIL said negative.</span></p>\r\n<p class="MsoCommentText" style="margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Kit asked BANK whether in contact with any chopper from HUKC to the west and BANK said yes I have given her traffic information. Kit said yes but she passed very close to TREE and she complained.</span></p>\r\n<p class="MsoCommentText" style="margin-top: 0in; margin-right: 0in; margin-bottom: 8.0pt; margin-left: .25in; text-indent: -.25in; mso-list: l5 level1 lfo2;"><!--[if !supportLists]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">8.<span style="font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span><!--[endif]--><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">At Wn49 MERID VAJA23 reported &ldquo;we have now crossed extended centerline 17 at 18 DME&rdquo;, was instructed to next report 65 DME, VAJA23 requested BANK to say again all over. BANK instructed her to report 50 DME and she acknowledged.</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l3 level1 lfo1;"><!--[if !supportLists]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">b.<span style="font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><!--[endif]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">SoDF</span></strong></p>\r\n<ol style="margin-top: 0in;" type="i" start="1">\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Traffic was handled by an Bukatrainee.</span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Both aerodrome control officers did their last OAZGYFJYU Operation checks on 11<sup>th</sup> November 2016.</span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Approach kit control officer last did OAZGYFJYU Operation check on 7<sup>th</sup> August 2015.</span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Both LOUNGEofficers last did LOUNGEOAZGYFJYU Operation checks in August 2015</span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Nil record of approach procedure control OAZGYFJYU Operation check undertaken by the duty BANK control and approach kit control officer. </span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Nil record of annual leave taken by BOIL KCOO11s</span></li>\r\n<li class="MsoNormal" style="margin-bottom: .0001pt; mso-list: l2 level1 lfo4;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Duty BANK KCOO11 last took annual rest in FDDil/2015.</span></li>\r\n</ol>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></p>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in; text-indent: -.25in; mso-list: l3 level1 lfo1;"><!--[if !supportLists]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">c.<span style="font-weight: normal; font-stretch: normal; font-size: 7pt; line-height: normal; font-family: ''Times New Roman'';">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; </span></span></strong><!--[endif]--><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">RPB <sup>6th June 2016</sup></span></strong></p>\r\n<p class="MsoNormal" style="margin-top: 0in; margin-right: 0in; margin-bottom: .0001pt; margin-left: .25in;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></p>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Bukatrainee requested Approach kit to give her the traffic so that she handles it so she can continue practicing. [Teresa&ndash; APC procedure trainee]</span></p>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></p>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">MELU11 was transponding thus visible on the SDD, yet VAJA23 was not and therefore not visible on the SDD</span></p>\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp; </span></p>\r\n<table class="MsoNormalTable" style="margin-left: -21.0pt; border-collapse: collapse; border: none; mso-border-alt: solid windowtext .5pt; mso-yfti-tbllook: 1184; mso-padding-alt: 0in 5.4pt 0in 5.4pt; mso-border-insideh: .5pt solid windowtext; mso-border-insidev: .5pt solid windowtext;" border="1" cellspacing="0" cellpadding="0">\r\n<tbody>\r\n<tr>\r\n<td style="border: solid windowtext 1.0pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></strong></p>\r\n</td>\r\n<td style="width: 220.75pt; border: solid windowtext 1.0pt; border-left: none; mso-border-left-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top" width="294">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">MELU11</span></strong></p>\r\n</td>\r\n<td style="width: 243.9pt; border: solid windowtext 1.0pt; border-left: none; mso-border-left-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top" width="325">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><strong><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></strong></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: solid windowtext 1.0pt; border-top: none; mso-border-top-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">Wn46</span></p>\r\n</td>\r\n<td style="width: 220.75pt; border-top: none; border-left: none; border-bottom: solid windowtext 1.0pt; border-right: solid windowtext 1.0pt; mso-border-top-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top" width="294">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">18 miles NN 065 descending</span></p>\r\n</td>\r\n<td style="width: 243.9pt; border-top: none; border-left: none; border-bottom: solid windowtext 1.0pt; border-right: solid windowtext 1.0pt; mso-border-top-alt: solid windowtext .5pt; mso-border-left-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; padding: 0in 5.4pt 0in 5.4pt;" valign="top" width="325">\r\n<p class="MsoNormal" style="margin-bottom: .0001pt;"><span style="font-size: 12.0pt; line-height: 115%; mso-bidi-font-family: Calibri;">&nbsp;</span></p>\r\n</td>\r\n</tr>\r\n<tr>\r\n<td style="border: solid windowtext 1.0pt; border-top: none; mso-border-top-alt: solid windowtext .5pt; mso-border-alt: solid windowtext .5pt; pad